$(function() {
	var koefHeig = $('.carousel-cell').children().height() * 25 / 100;
	$('.carousel').height($('.carousel-cell').children().height() + koefHeig);
	$(window).resize(function(){
		$('.carousel').height($('.carousel-cell').children().height() + koefHeig);
	});


});
$(function() {
	$('.device-block').height($('.device-block').children().height());
	$(window).resize(function(){
		$('.device-block').height($('.device-block').children().height());
	});
});
